const h = g.getHeight();
const w = g.getWidth();

var img = require("heatshrink").decompress(atob(""));

function draw() {
  var date = new Date();
  var timeStr = require("locale").time(date,1);
  
  g.reset();
  g.setColor(g.theme.bg);
  g.fillRect(Bangle.appRect);

  g.drawImage(img, 0, 0);

  g.setFont('Vector', w/4);
  g.setFontAlign(0, 0);
  g.setColor(g.theme.fg);
  g.drawString(timeStr, w/2, h/2);

}

g.clear();
Bangle.setUI("clock");
draw();
