Graphics.prototype.setFontKdamThmor = function(scale) {
  // Actual height 72 (71 - 0)
  g.setFontCustom(atob(""), 46, atob("FCM0NDQ0NDQ0NDQ0GA=="), 90+(scale<<8)+(1<<16));
}

const SETTINGS_FILE = "pebble.json";
let settings;

function loadSettings() {
  settings = require("Storage").readJSON(SETTINGS_FILE,1)|| {'bg': '#0f0', 'color': 'Green'};
}

const h = g.getHeight();
const w = g.getWidth();
const ha = 2*h/5 - 8;
const h2 = 3*h/5 - 10;
const h3 = 7*h/8;

//const w2 = 4*w/5;
//const w2 = 6*w/8;
//const w3 = 7*w/8;

const w2 = 5*w/7;
const w3 = 6*w/7;

function draw() {
  let date = new Date();
  let da = date.toString().split(" ");
  let hh = da[4].substr(0,2);
  let mm = da[4].substr(3,2);
  const t = 6;

  g.reset();
  g.setColor(g.theme.bg);
  g.fillRect(0, 0, w2, h);
  g.setColor(settings.bg);
  g.fillRect(w2, 0, w, h);

  // time
  g.setColor(g.theme.fg);
  g.setFontKdamThmor();
  g.setFontAlign(0, -1);
  g.drawString(hh, w2/2, 10 + 0);
  g.drawString(mm, w2/2, 10 + h/2);

  // day and steps
  g.setColor('#fff');
  g.setFont('6x8', 2);
  g.setFontAlign(0, -1);
  g.drawString(da[0].toUpperCase(), w3, 2*h/4);
  g.drawString(getSteps(), w3, 3*h/4);

  drawCalendar(w2 + 8, h/8, w/5, 3, da[2]);

}

// at x,y width:wi thicknes:th
function drawCalendar(x,y,wi,th,str) {
  g.setColor(g.theme.fg);
  g.fillRect(x, y, x + wi, y + wi);
  g.setColor(g.theme.bg);
  g.fillRect(x + th, y + th, x + wi - th, y + wi - th);
  g.setColor(g.theme.fg);

  let hook_t = 6;
  // first calendar hook, one third in
  g.fillRect(x + (wi/3) - (th/2), y - hook_t, x + wi/3 + th - (th/2), y + hook_t);
  // second calendar hook, two thirds in
  g.fillRect(x + (2*wi/3) -(th/2), y - hook_t, x + 2*wi/3 + th - (th/2), y + hook_t);

  g.setFont('Vector', 22);
  g.setFontAlign(0, 0);
  g.drawString(str, x + wi/2 + th/2, y + wi/2 + th/2);
}

function getSteps() {
  if (WIDGETS.wpedom !== undefined) {
    return WIDGETS.wpedom.getSteps();
  }
  return '????';
}

g.clear();
Bangle.loadWidgets();
/*
 * we are not drawing the widgets as we are taking over the whole screen
 * so we will blank out the draw() functions of each widget
 */
for (let wd of WIDGETS) {wd.draw=()=>{};}
loadSettings();
setInterval(draw, 15000); // refresh every 15s
draw();
Bangle.setUI("clock");
